import React from 'react'
import { useAuth0 } from '@auth0/auth0-react'
import {LoginButton} from '../components/LogInFunction'
import {LogoutButton} from '../components/LogOutFunction'
import {Profile} from '../components/Profile'

import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

function Modedark() {
  var element = document.body;
  element.classList.add("dark-mode");
  element.classList.remove("light-mode");
}

function ModeLight() {
  var element = document.body;
  element.classList.add("light-mode");
  element.classList.remove("dark-mode");

}

function Login() {

    const {isAuthenticated } = useAuth0();

        return (
            <>
            <div className='div-body'>
                <br/>
                {isAuthenticated ? (
                    <>
                        <Profile/>
                        <LogoutButton/>  
                    </>                      
            ) : (
                <LoginButton/> 
            )}
            </div>  

            <div>
            <div className='cardtheme2'>
        <a>Dark</a>
        <Card style={{ width: '5rem' }}>
          <Card.Img variant="top" src="tate.jpg" />
            <Button variant="dark" onClick={Modedark}>Váltás</Button>
        </Card>
        <br />

        <a>dsad</a>
        <Card style={{ width: '5rem' }}>
          <Card.Img variant="top" src="tate.jpg" />
            <Button variant="primary">Váltás</Button>
        </Card>
        <br />
      </div>

      <div className='cardtheme1'>
        <a>dsadfd</a>
        <Card style={{ width: '5rem' }}>
          <Card.Img variant="top" src="tate.jpg" />
            <Button variant="light" onClick={ModeLight}>Váltás</Button>
        </Card>
        
      </div>
    </div>

    </>
        )
            }

export default Login
