import React, { useState } from "react";
import { Button } from "react-bootstrap";

function szamol() {
  var a, b;
}

function Mester() {
  const [data, setData] = useState(null);
  const [print, setPrint] = useState(false);
  function getData(val) {
    setData(val.target.value);
    setPrint(false);
    console.write(val.target.value);
  }
  return (
    <div className="test">
      <h1>Mestertétel</h1>
      <br />
      <div className="mesterinputa">
        <label>
          T értéke:
          <input type="text" onChange={getData} className="mesterinput" />
        </label>
      </div>
      <br />
      <br />
      <div className="mesterinputb">
        <label>
          <a>n-es tag:</a>
          <input type="text" onChange={getData} className="mesterinput" />
          <br />
          <Button onClick={() => setPrint(true)} variant="secondary">
            Megold
          </Button>
        </label>
      </div>
    </div>
  );
}

export default Mester;
